public class stringManipulation {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1 = "shaloo";
		String s2 = "amit";
		int s3 =10 , s4 =20;
		System.out.println(s1+s2+s3+s4);
		System.out.println(s1+s2+(s3+s4));
		System.out.println(s3+s4+s1+s2);
		System.out.println((s3+s4)+s1+s2);
	}

}
